import { Card, CardActionArea, CardContent, CardMedia, Grid, Typography } from "@mui/material";
import React from "react";
import { useState } from "react";
import "./App";
import Categories from "./Categories";

const Category = (props) => {
  const [data, setData] = useState(Categories);
  const { image, description, price, rating, category, title} = val;


  const filterData=(categeoryItem)=>{
    const result = Categories.filter((currentData)=>{
        return currentData.category===categeoryItem;
    });
    setData(result)
  }
  return (

    <>
      <h1>Shopping App</h1>
      <button onClick={()=>{setData(Categories)}}>All</button>
      <button onClick={()=>{filterData("men's clothing")}}>Men's Clothing</button>
      <button onClick={()=>{filterData("women's clothing")}}>Women's Clothing</button>
      <button onClick={()=>{filterData("electronics")}}>Electronics</button>
      <button onClick={()=>{filterData("jewelery")}}>Jewellary</button>
      <div>




            <Grid container spacing={1} >
              <Grid item xs>
            <Card sx={{maxWidth: 340,}}>
              <Typography gutterBottom variant="h5" component="">
                  <h6> S.No {props.id} - {props.category}</h6> 
                  </Typography>
              <CardActionArea>
                <CardMedia 
                component="img"
                height="470"
                image={props.CardMediaimage}
                />
                <CardContent>
                  <Typography>
                    <span>Title</span><h2>{props.title} </h2>
                  </Typography>
                  <Typography>
                    <h3>$ {props.price}</h3>
                  </Typography>
                  <p>Rating - {props.rating.rate} </p>
                  <Typography variant="body2" color="text.secondary">
                   {props.description}
                  </Typography>
                </CardContent>
              </CardActionArea>
            </Card>

            </Grid>
            </Grid> 
      </div>
    </>
  );
};

export default Category;
