import { Box, Button, Card, CardActionArea, CardContent, CardMedia, Grid, InputBase, Typography } from "@mui/material";
import ShoppingCartTwoToneIcon from '@mui/icons-material/ShoppingCartTwoTone';
import React from "react";
import { useState } from "react";
import "./App";
import Categories from "./Categories";
import SearchIcon from '@mui/icons-material/Search';
import { alpha, styled } from '@mui/material/styles';

const Category = () => {
  const [data, setData] = useState(Categories);
  const [state, setState] =useState([])
  const [fileteredata, setFilteredData] = useState("");

  const filterData=(categeoryItem)=>{
    const result = Categories.filter((currentData)=>{
        return currentData.category===categeoryItem;
    });
    setData(result)
  }
  const SearchFilterData=(e)=>{
    const filterResult= Categories.filter(item => {
      if (!value) return true;
      if (item.title.includes(fileteredata) || item.text.includes(fileteredata)) {
        return true;
      }
      return false;
  })
  setFilteredData(filterResult)
  const Search = styled('div')(({ theme }) => ({
    position: 'relative',
    margin:10,
    borderRadius: theme.shape.borderRadius,
    backgroundColor: alpha(theme.palette.common.black, 0.15),
    '&:hover': {
      backgroundColor: alpha(theme.palette.common.black, 0.25),
    },
    marginRight: theme.spacing(40),
    backgroundColor:"Highlight",
    [theme.breakpoints.up('md')]: {
      marginLeft: theme.spacing(40),

    },
  }));

  const SearchIconWrapper = styled('div')(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  }));

  const StyledInputBase = styled(InputBase)(({ theme }) => ({
    color: 'inherit',
    '& .MuiInputBase-input': {
      padding: theme.spacing(1, 1, 1, 0),
      // vertical padding + font size from searchIcon
      paddingLeft: `calc(1em + ${theme.spacing(4)})`,
      transition: theme.transitions.create('width'),
      width: '100%',
      [theme.breakpoints.up('md')]: {
        width: '20ch',
      },
    },
  }));

 const  onChangeHandler=(e)=>{
    console.log(e.target.value);
    var newArray = Categories.filter((d)=>{
      return d.indexOf(e.target.value) !== -1 
    });
    console.log(newArray)
     setState({
      Categories:newArray
    })
  }


  return (
    <>
      <h1>Shopping App</h1>
      <div style={{display:"flex", justifyContent:"right", marginRight:45}}>Shop <ShoppingCartTwoToneIcon /></div>
      <Box sx={{display:"flex",gap:1, alignItems:"center", justifyContent:"center"}}>
      <Button variant="contained" onClick={()=>{setData(Categories)}}>All</Button>
      <Button variant="contained" onClick={()=>{filterData("men's clothing")}}>Men's Clothing</Button>
      <Button variant="contained" onClick={()=>{filterData("women's clothing")}}>Women's Clothing</Button>
      <Button variant="contained" onClick={()=>{filterData("electronics")}}>Electronics</Button>
      <Button variant="contained" onClick={()=>{filterData("jewelery")}}>Jewellary</Button>
      </Box>
      <div style={{ marginLeft:0}}>
        <Search value={value} onChange={SearchFilterData}>
            <SearchIconWrapper >
              <SearchIcon />
            </SearchIconWrapper>
            <StyledInputBase
              placeholder="Search…"
              inputProps={{ 'aria-label': 'search' }}
            />
        </Search>
      </div>
      <div style={{marginLeft:80, marginTop:20,}} >
        <Grid container>
        {data.map((val, ind) => {
            const {id , image, description, price, rating, category, title} = val;
          return (
            <div style={{margin:10,}} key={ind}>
              <Grid item>
            <Card sx={{maxWidth: 250, height:600}} key={ind}>
              <Typography>
                  <h5> S.No {id} - {category}</h5> 
                  </Typography>
              <CardActionArea>
                <CardMedia 
                component="img"
                height="320"
                image={image}
                />
                <CardContent>
                  <Typography>
                    <b>{title}</b>
                  </Typography>
                  <Typography>
                    $ {price}
                  </Typography>
                Rating - {rating.rate} 
                </CardContent>
              </CardActionArea>
              <Button variant="contained" color="warning"><ShoppingCartTwoToneIcon/> Add to Cart</Button>
            </Card>
            </Grid>
            </div> 
          );
        })}
        </Grid> <br />
      </div>
    </>
  );
}}

export default Category

