import { Card, CardActionArea, CardContent, CardMedia, Grid, Typography } from "@mui/material";
import React from "react";
import { useState } from "react";
import "./App";
import Categories from "./Categories";

const Category = () => {
  const [data, setData] = useState(Categories);

  const filterData=(categeoryItem)=>{
    const result = Categories.filter((currentData)=>{
        return currentData.category===categeoryItem;
    });
    setData(result)
  }
  return (
    <>
      <h1>Shopping App</h1>
      <button onClick={()=>{setData(Categories)}}>All</button>
      <button onClick={()=>{filterData("men's clothing")}}>Men's Clothing</button>
      <button onClick={()=>{filterData("women's clothing")}}>Women's Clothing</button>
      <button onClick={()=>{filterData("electronics")}}>Electronics</button>
      <button onClick={()=>{filterData("jewelery")}}>Jewellary</button>
      <div>
        {data.map((val, ind) => {
            const {id , image, description, price, rating, category, title} = val;
          return (
            <Grid container  row>
            <Card sx={{maxWidth: 340,}} key={ind}>
              <Typography gutterBottom variant="h5" component="div">
                  <h6> S.No {id} - {category}</h6> 
                  </Typography>
              <CardActionArea>
                <CardMedia 
                component="img"
                height="470"
                image={image}
                />
                <CardContent>
                  <Typography>
                    <span>Title</span><h2>{title} </h2>
                  </Typography>
                  <Typography>
                    <h3>$ {price}</h3>
                  </Typography>
                  <p>Rating - {rating.rate} </p>
                  <Typography variant="body2" color="text.secondary">
                   {description}
                  </Typography>
                </CardContent>
              </CardActionArea>
            </Card>
            </Grid>
          );
        })}
      </div>
    </>
  );
};

export default Category;
